import Layout from '../components/MyLayout';
import Sidebar from '../components/Sidebar';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';
import axios from 'axios';
import Router from 'next/router';
const Parks = props => (
  <Layout>

    <div className="container">
    <h3>Parks</h3>
    <div className="row">
      <div className="col-md-7">
      <table className="table">
        <thead>
          <tr>
            <th scope="col">Name</th>
            <th scope="col">Region</th>
            <th scope="col">Actions</th>
          </tr>
        </thead>
        <tbody>
            {props.parks.map(park => (
                  <tr key={park.pid}>
                    <td>
                      <a>{park.location_info.name}</a>
                    </td>
                    <td>
                      <a>{park.location_info.region}</a>
                    </td>
                    <td>
                      <Link href="/parks/[id]" as={`/parks/${park.pid}`}>
                        <a role="button" className="btn btn-info">View</a>
                      </Link>
                      <span/>
                        {/* <button type="button" className="btn btn-success" data-toggle="modal" data-target="#parkUpdateModal">Update</button> */}
                        <button type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModalScrollable">
  Launch demo modal
</button>

{/* <!-- Modal --> */}
<div class="modal fade" id="exampleModalScrollable" tabindex="-1" role="dialog" aria-labelledby="exampleModalScrollableTitle" aria-hidden="true">
  <div class="modal-dialog modal-dialog-scrollable" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalScrollableTitle">Modal title</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        ...
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>
                      <button type="button" className="btn btn-danger" onClick={
                          event => {
                          event.preventDefault();
                          axios.delete(`http://localhost:8080/parkpay/parks/${park.pid}`)
                          .then(()=>{
                            Router.push('/parks')
                          })
                          .catch(err => {
                            console.log(err);
                          })
                        }
                      }>Delete</button>
                    </td>
                  </tr>
              ))}

        </tbody>
      </table>
      </div>
      <Sidebar />
    </div>
    </div>
  </Layout>
);

Parks.getInitialProps = async function() {
  const res = await fetch('http://localhost:8080/parkpay/parks');
  const data = await res.json();
  console.log(data.parks ? 'Hello' : data.parks)

  return {
    parks: data
  };
};

export default Parks;
