import React from 'react'
import Layout from '../../components/MyLayout'
import axios from 'axios';

export default class Registration extends React.Component {
    state = {
        name:'',
        location_info:{
            name: '',
            region: '',
            address: '',
            phone: '',
            web: '',
            geo: '',
        },
        payment_info: {
            motorcycle: [0,0],
            car : [0,0],
            rv : [0,0]
        }
    }

    handleChange = event => {
        this.setState({ [event.target.name] : { name : event.target.value }} );
    }

    handleSubmit = event => {
        event.preventDefault();

        const user = {
            name: this.state.name,

        };

        axios.post(`http://localhost/parkpay/parks`,{user})
        .then(res => {

        })
    }

    render(){
        return(
            <Layout>
                <div>
                    <h1>Register new park</h1>
                <form onSubmit={this.handleSubmit}>
                <label>
                    Person Name:
                    <input type="text" name="location_info" dest="name" value={this.state.location_info.name} onChange={this.handleChange} />
                </label>
                <button type="submit">Add</button>
                </form>
            </div> 
          </Layout>
        )
    }

}
