import Layout from '../components/MyLayout';
import fetch from 'isomorphic-unfetch';

const Order = props => (
  <Layout>
    <h1>Orders</h1>
    <ul>
        {props.orders ? 
            props.orders.map(order => (
                <li key={order.oid}>
                    <Link href="/orders/[id]" as={`/p/${order.nid}`}>
                        <a>{order.oid}</a>
                    </Link>
                </li>
            ))
            :
            "No order yet"
        }
    </ul>
    <Note />
  </Layout>
);

Order.getInitialProps = async function(context) {
  const { id } = context.query;
  const res = await fetch(`http://localhost:8080/parkpay/orders`);
  const orders = await res.json();

  return { orders };
};

export default Post;
