import Layout from '../components/MyLayout';
import Alert from '../components/Alert';
import TitleActionBar from '../components/TitleActionBar';
import Link from 'next/link';
import fetch from 'isomorphic-unfetch';
import axios from 'axios';

const Index = props => (
  <Layout>
    <TitleActionBar title="Parks"/>
    {props.parks ? 
    <table className="table">
      <thead>
        <tr>
          <th scope="col">Name</th>
          <th scope="col">Region</th>
          <th scope="col">Actions</th>
        </tr>
      </thead>
      <tbody>
          {props.parks.map(park => (
                <tr>
                  <td>
                    <a>{park.location_info.name}</a>
                  </td>
                  <td>
                    <a>{park.location_info.region}</a>
                  </td>
                  <td>
                    <Link href="/parks/[id]" as={`/parks/${park.pid}`}>
                      <a role="button" className="btn btn-info">View</a>
                    </Link>
                    <span/>
                    <Link href="/parks/update/[id]" as={`/parks/update/${park.pid}`}>
                      <button type="button" className="btn btn-success">Update</button>
                    </Link>
                    <button type="button" className="btn btn-danger" onClick={
                      handleDelete = event => {
                        event.preventDefault();
                        axios.delete(`http://localhost:8080/parkpay/parks/${park.pid}`)
                        .then(response=>{
                          console.log(response)
                        })
                        .catch(err => {
                          console.log(err);
                        })
                      }
                    }>Delete</button>
                  </td>
                </tr>
            ))}

      </tbody>
    </table>
    :
     <Alert type="danger" text="No park data yet!"></Alert>
    }
  </Layout>
);

Index.getInitialProps = async function() {
  const res = await fetch('http://localhost:8080/parkpay/parks');
  const data = await res.json();
  console.log(data.parks)

  return {
    parks: data
  };
};

export default Index;
