import Layout from '../../components/MyLayout';
import fetch from 'isomorphic-unfetch';

const Order = props => (
  <Layout>
    <h1></h1>
    <ul>
        <li>{props.order.oid}</li>
        <li>{props.order.pid}</li>
        <li>{props.order.amount}</li>
        <li>{props.order.vid}</li>
        <li>{props.order.date}</li>
        <li>{props.order.vehicle.state}</li>
        <li>{props.order.vehicle.plate}</li>
        <li>{props.order.vehicle.type}</li>
        <li>{props.order.visitor.name}</li>
        <li>{props.order.visitor.email}</li>
        <li>{props.order.visitor.payment_info.card}</li>
        <li>{props.order.visitor.payment_info.name_on_card}</li>
        <li>{props.order.visitor.payment_info.zip}</li>
        <li>{props.order.payment_processing.card_transaction_id}</li>
        <li>{props.order.payment_processing.date_and_time}</li>
    </ul>
  </Layout>
);

Order.getInitialProps = async function(context) {
  const { id } = context.query;
  const res = await fetch(`http://localhost:8080/parkpay/orders/${id}`);
  const order = await res.json();

  return { order };
};

export default Order;
