import Layout from '../../components/MyLayout';
import fetch from 'isomorphic-unfetch';

const Search = props => (
  <Layout>
    <h3>Search : {props.input}</h3>
    {props.parks.map(park => (
        <p>{park.location_info.name}</p>
    ))}
  </Layout>
);

Search.getInitialProps = async function(context) {
    const { input } = context.query;
    const res_park = await fetch(`http://localhost:8080/parkpay/parks?key=${input}`);
    const parks = await res_park.json();
    const res_note = await fetch(`http://localhost:8080/parkpay/notes?key=${input}`);
    const notes = await res_note.json();
    const res_order = await fetch(`http://localhost:8080/parkpay/notes?key=${input}`);
    const orders = await res_order.json(); 
    const res_visitor = await fetch(`http://localhost:8080/parkpay/visitors?key=${input}`);
    const visitors = await res_visitor.json()
    return { input, parks, notes, orders, visitors };
};

export default Search;
