import Layout from '../../components/MyLayout';
import fetch from 'isomorphic-unfetch';

const Visitor = props => (
  <Layout>
    <h1>{props.visitor.visitor.name}</h1>
    <ul>
      <li>{props.visitor.visitor.email}</li>
      <li>Orders</li>
      <ul>
          {props.visitor.orders ?
          props.visitor.orders.map(order => (
              <li key={order.oid}>
                  {order.oid},{order.pid},{order.date}
              </li>
          ))
        :
        "No order yet"
        }
      </ul>
      <li>Notes</li>
      <ul>
          {props.visitor.notes ?
            props.visitor.notes.map(note => (
                <li key={note.nid}>
                    {note.date} , {note.title}
                </li>
            ))
            :
            "No note yet"
          }
      </ul>
    </ul>
  </Layout>
);

Visitor.getInitialProps = async function(context) {
  const { id } = context.query;
  const res = await fetch(`http://localhost:8080/parkpay/visitors/${id}`);
  const visitor = await res.json();

  return { visitor };
};

export default Visitor;
