import Layout from '../components/MyLayout';
import Alert from '../components/Alert';
import Link from 'next/link'
import fetch from 'isomorphic-unfetch';

const Orders = props => (
  <Layout>
    <h1>Orders</h1>
    {props.orders ?
        <table class="table">
        <thead>
          <tr>
            <th scope="col">Name</th>
            <th scope="col">E-mail</th>
            <th scope="col">Actions</th>
          </tr>
        </thead>
        <tbody>
            {props.visitors.map(visitor => (
                  <tr>
                    <td>
                      <a>{visitor.name}</a>
                    </td>
                    <td>
                      <a>{visitor.email}</a>
                    </td>
                    <td>
                      <Link href="/orders/[id]" as={`/orders/${order.oid}`}>
                        <a role="button" class="btn btn-success">View</a>
                      </Link>
                      <span/>
                      <button type="button" class="btn btn-danger">Delete</button>
                    </td>
                  </tr>
              ))}

        </tbody>
      </table>
    :
    <Alert type="danger" text="No order yet!"></Alert>
    }
  </Layout>
);

Orders.getInitialProps = async function(context) {
  const res = await fetch(`http://localhost:8080/parkpay/orders`);
  const orders = await res.json();

  return { orders };
};

export default Orders;
